$(document).ready(function(){$("body").on("click",".save_article",function(){var e=$(this).closest(".new_article").serialize();return $.ajax({url:"articles",type:"post",data:e,success:function(e){$("#articles_form").remove(),$("#error_explanation").remove(),$("#new_article").append(e)}}),!1}),$("body").on("click",".save_editarticle",function(){var e=$(this).closest(".edit_article").serialize();return $.ajax({url:"articles",type:"post",data:e,success:function(e){$("#articles_form").remove(),$("#error_explanation").remove(),$("#new_article").append(e)}}),!1})});